function Android_Toast(e){this.timeout_id=null;this.duration=3e3;this.content="";this.position="bottom";if(e&&typeof e=="object"){if(e.duration){this.duration=parseInt(e.duration)}if(e.content){this.content=e.content}if(e.position){position=e.position.toLowerCase();switch(position){case"top":case"bottom":this.position=position;break;default:this.position="bottom";break}}}}Android_Toast.prototype.show=function(){if(!this.content){return false}clearTimeout(this.timeout_id);var e=document.getElementsByTagName("body")[0];var t=document.getElementById("android_toast_container");if(t){e.removeChild(t)}var n=document.createElement("div");n.setAttribute("id","android_toast_container");n.setAttribute("class","android_toast_fadein");if(this.position=="top"){n.setAttribute("class","android_toast_fadein android_toast_top")}e.appendChild(n);var r=document.createElement("div");r.setAttribute("id","android_toast");r.innerHTML=this.content;n.appendChild(r);this.timeout_id=setTimeout(this.hide,this.duration);return true};Android_Toast.prototype.hide=function(){var e=document.getElementById("android_toast_container");if(!e){return false}clearTimeout(this.timeout_id);e.className+=" android_toast_fadeout";e.addEventListener("webkitAnimationEnd",function(){e.parentNode.removeChild(e)});return true}